import React from 'react';
import './App.css';
import ImageUpload from './ImageUpload.js';
import ShowResponse from './ShowResponse.js';

class App extends React.Component{

    constructor (props) {
      super (props)
      this.setResponse = this.setResponse.bind(this)
      this.setState()
    }

    componentDidMount() {
      this.setState({imageText: null})
    }

    setResponse = (responseText) => {
        this.setState({imageText: responseText})
    }

    render() {
        return (
          <div className="App">
            <ImageUpload setResponse={this.setResponse}/>
            if (this.state.imageText) {
                <ShowResponse text={this.state.imageText}/>
            }
          </div>
        );
    }

}

export default App;
